/**
 * yay this has code from the httppost and postfields test cases.
 * This was done because the duplicated handle shares the ToFree instances of the original handle,
 *  so we need to test their behavior when a handle is duplicated.
 * We can test that easily by testing HTTPPOST and POSTFIELDS.
 */
import 'should'

import fs from 'fs'
import path from 'path'

import querystring from 'querystring'
import multiparty from 'multiparty'

import { app, host, port, server } from '../helper/server'
import { Curl } from '../../lib'

const image =
  ''
const imageFilename = path.resolve(__dirname, 'upload.png')

const buffer = Buffer.from(image, 'base64')
const size = buffer.length

const httpPostData = [
  {
    name: 'file',
    file: imageFilename,
    type: 'image/png',
  },
]
const postFieldsData: { [key: string]: string } = {
  'input-name': 'This is input-name value.',
  'input-name2': 'This is input-name2 value',
}

const url = `http://${host}:${port}`

let curl: Curl
let duplicatedCurl: Curl

describe('dupHandle()', () => {
  beforeEach(() => {
    curl = new Curl()
  })

  afterEach(() => {
    curl && curl.close()
    duplicatedCurl && duplicatedCurl.close()
  })

  before(done => {
    server.listen(port, host, () => {
      fs.writeFile(imageFilename, buffer, done)
    })

    app.post('/urlencoded', (req, res) => {
      res.send(JSON.stringify(req.body))
    })

    app.post('/multipart', (req, res) => {
      // parse a file upload
      const form = new multiparty.Form()

      form.parse(req, (error, fields, files) => {
        if (error) {
          throw error
        }

        const file = files.file

        const response = {
          size: file[0].size,
          name: file[0].originalFilename,
          type: file[0].headers['content-type'],
        }

        res.send(JSON.stringify(response))
      })
    })
  })

  after(done => {
    server.close()

    app._router.stack.pop()
    app._router.stack.pop()

    fs.unlink(imageFilename, done)
  })

  it('should correctly send HTTPPOST data when duplicated', done => {
    curl.setOpt('URL', `${url}/multipart`)
    curl.setOpt('HTTPPOST', httpPostData)

    duplicatedCurl = curl.dupHandle()
    // close original curl to check if data is removed
    curl.close()
    curl = new Curl()

    duplicatedCurl.on('end', (status, data) => {
      if (status !== 200) {
        throw Error('Invalid status code: ' + status)
      }

      const parsedData = JSON.parse(data as string)

      parsedData.size.should.be.equal(size)
      parsedData.name.should.be.equal(imageFilename.split(path.sep).pop())
      parsedData.type.should.be.equal(httpPostData[0].type)

      done()
    })

    duplicatedCurl.on('error', done)

    duplicatedCurl.perform()
  })

  it('should correctly send POSTFIELDS data when duplicated', done => {
    curl.setOpt('URL', `${url}/urlencoded`)
    curl.setOpt('POSTFIELDS', querystring.stringify(postFieldsData))

    duplicatedCurl = curl.dupHandle()
    // close original curl to check if data is removed
    curl.close()
    curl = new Curl()

    duplicatedCurl.on('end', (status, data) => {
      if (status !== 200) {
        throw Error('Invalid status code: ' + status)
      }

      const parsedData = JSON.parse(data as string)

      for (const field in parsedData) {
        if (parsedData.hasOwnProperty(field)) {
          parsedData[field].should.be.equal(postFieldsData[field])
        }
      }

      done()
    })

    duplicatedCurl.on('error', done)

    duplicatedCurl.perform()
  })
})
